import { Options, LocalsObject } from 'pug';
import { Logger, Plugin } from 'vite';

declare type PluginOptions = Options & {
    localImports?: boolean | ((htmlfile: string) => boolean);
};
declare function pugs(html: string, pugger: (filename: string) => string, logger?: Pick<Logger, "warn">): string;
declare function export_default(options?: PluginOptions, locals?: LocalsObject): Plugin;

export { PluginOptions, export_default as default, pugs };
